mapper.insert:
insert into club (id, name, userId, 
  nickName, icoUrl, time
  )
values (#{id,jdbcType=INTEGER}, #{name,jdbcType=VARCHAR}, #{userId,jdbcType=VARCHAR}, 
  #{nickName,jdbcType=VARCHAR}, #{icoUrl,jdbcType=VARCHAR}, #{time,jdbcType=TIMESTAMP}
  )

mapper.select:
select id, name, userId, nickName, icoUrl, time
from club
where id = #{id,jdbcType=INTEGER}
